"use strict";

var itemsReview = document.querySelectorAll('.casino__item-reviews');
itemsReview.forEach(function (item) {
  var buttonItem = item.querySelector('.casino__item-reviews-showMobile-text');
  var infoItem = item.querySelectorAll('.casino__item-reviews-list');
  var imagesBtn = item.querySelector('.casino__item-reviews-showMobile-icon');
  var showMobile = item.querySelector('.casino__item-reviews-showMobile');
  buttonItem.addEventListener('click', function () {
    !showMobile.classList.contains('_open') ? buttonItem.textContent = 'Hide' : buttonItem.textContent = 'Pros and cons';
    imagesBtn.classList.toggle('_none');
    showMobile.classList.toggle('_open');
    infoItem.forEach(function (info) {
      info.classList.toggle('_open');
    });
  });
});
"use strict";
